import { createStore } from 'vuex'
import axios from 'axios'
// Create a new store instance.
const store = createStore({
    state() {
        return {
            workouts: null,
            user: localStorage.getItem('user'),
            error: null,
            isError: false
        }
    },
    mutations: {
        logout(state) {
            state.error = null
            state.isError = null
            state.user = null
        }
    },
    actions: {
        logout(context) {
            context.commit('logout')
        }
    }
})
export default store;

