import { createStore } from 'vuex'

// Create a new store instance.
const store = createStore({
    state() {
        return {
            title: null,
            Body: null,
            Content: null,
            Code: null
        }
    },
    mutations: {
        onsubmit(state) {
            axios.post('http://localhost:4000/books', {
                title: state.title,
                Body: state.Body,
                Content: state.Content,
                Code: state.Code
            })
                .then((res) => {
                    console.log(res);
                    // window.location.reload()
                })
                .catch((err) => {
                    console.log(err);
                })

        }
    },
    actions: {
        onsubmit (context) {
          context.commit('onsubmit')
        }
    }
})
export default store;
